INSERT INTO `wp_posts` VALUES(876, 1, '2013-11-11 08:56:35', '2013-11-11 16:56:35', 'Please join us, friends and family, one and all, to remember and celebrate the life of our beloved <a href="https://www.facebook.com/spencer.wysinger?directed_target_id=0" target="_blank">Spencer Wysinger</a>. Gone too soon but never forgotten...\r\n\r\nWednesday, November 13, 2013 7pm at the <a href="https://www.facebook.com/thesfeagle?directed_target_id=0" target="_blank">SF Eagle</a> 398 12th Street, SF, CA 94103\r\n\r\nWe will be playing some of Spencer''s favorite music, showing some of his favorite movies and sharing our memories of him together.\r\n\r\nWe are also in the process of creating a photo album, and everyone is encouraged to share their pictures for inclusion. Spencer''s circle of friends and extended family reaches far and wide, and we would love for everyone to participate who wishes to.\r\n\r\nPlease send your photo memories of Spencer to: <a href="mailto:eduardo.wagar@gmail.com" target="_blank">eduardo.wagar@gmail.com</a>', 'Spencer Wysinger', '', 'inherit', 'closed', 'closed', '', '875-revision-v1', '', '', '2013-11-11 08:56:35', '2013-11-11 16:56:35', '', 875, 'http://sf-eagle.com/storm/2013/11/875-revision-v1/', 0, 'revision', '', 0);
