INSERT INTO `wp_posts` VALUES(940, 1, '2013-11-30 16:03:42', '2013-12-01 00:03:42', 'Category: DRINK SPECIAL\r\nAppears just after the logo and navigation buttons\r\nTitle will not appear but add one anyway so you can find the post again\r\nAdd short text and an image if you want (Text ABOVE the image looks best)\r\nDO NOT add a Featured Image\r\nPublish: set a future time when you want the special to end (military time: 10pm = 22:00)\r\n\r\nCategory: TEASE NOW\r\nreplaces the current tease image until this one expires (publish time)\r\n\r\nTitle: appears in large letters at the top center of the page\r\nFeatured Image: appears large in the center of the page\r\nYou can add text, links, other images, etc (all appear below Featured Image)\r\nVisilbility: Public to display. Private to hide\r\nPublish: set to future time when you want it to expire (military time: 10pm = 22:00)', 'HOW TO ADD TO THE WEBPAGE', '', 'inherit', 'closed', 'closed', '', '544-revision-v1', '', '', '2013-11-30 16:03:42', '2013-12-01 00:03:42', '', 544, 'http://sf-eagle.com/storm/2013/11/544-revision-v1/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(941, 1, '2013-11-30 16:04:07', '2013-12-01 00:04:07', '<p style="text-align: left;">Spencer Wysinger 1979-2013             <a title="Pics of Spencer" href="https://plus.google.com/105317909459568163402/posts" target="_blank">(more pics)</a></p>\r\nPlease join us, friends and family, one and all, to remember and celebrate the life of our beloved <a href="https://www.facebook.com/spencer.wysinger?directed_target_id=0" target="_blank">Spencer Wysinger</a>. Gone too soon but never forgotten...\r\n\r\n<a title="Spencer Wysinger Memorial" href="https://www.facebook.com/events/229385160562898/" target="_blank">Wednesday, November 13, 2013 7pm at the SF Eagle 398 12th Street, SF, CA 94103</a>\r\n\r\nWe will be playing some of Spencer''s favorite music, showing some of his favorite movies and sharing our memories of him together.\r\n\r\nWe are also in the process of creating a photo album, and everyone is encouraged to share their pictures for inclusion. Spencer''s circle of friends and extended family reaches far and wide, and we would love for everyone to participate who wishes to.\r\n\r\nPlease send your photo memories of Spencer to: <a href="mailto:wysingerspencer@gmail.com" target="_blank">wysingerspencer@gmail.com</a>', 'Spencer', '', 'inherit', 'closed', 'closed', '', '875-revision-v1', '', '', '2013-11-30 16:04:07', '2013-12-01 00:04:07', '', 875, 'http://sf-eagle.com/storm/2013/11/875-revision-v1/', 0, 'revision', '', 0);
