UNLOCK TABLES;

--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL auto_increment,
  `post_author` bigint(20) unsigned NOT NULL default '0',
  `post_date` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL default 'publish',
  `comment_status` varchar(20) NOT NULL default 'open',
  `ping_status` varchar(20) NOT NULL default 'open',
  `post_password` varchar(20) NOT NULL default '',
  `post_name` varchar(200) NOT NULL default '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL default '0',
  `guid` varchar(255) NOT NULL default '',
  `menu_order` int(11) NOT NULL default '0',
  `post_type` varchar(20) NOT NULL default 'post',
  `post_mime_type` varchar(100) NOT NULL default '',
  `comment_count` bigint(20) NOT NULL default '0',
  PRIMARY KEY  (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=70 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (1,1,'2013-07-02 18:51:36','2013-07-02 18:51:36','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2013-07-02 18:51:36','2013-07-02 18:51:36','',0,'http://sf-eagle.com/storm/wp/?p=1',0,'post','',1),(2,1,'2013-07-02 18:51:36','2013-07-02 18:51:36','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://sf-eagle.com/storm/wp/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','open','open','','sample-page','','','2013-07-02 18:51:36','2013-07-02 18:51:36','',0,'http://sf-eagle.com/storm/wp/?page_id=2',0,'page','',0),(51,1,'2013-07-11 05:15:09','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2013-07-11 05:15:09','0000-00-00 00:00:00','',0,'http://sf-eagle.com/storm/wp/?p=51',0,'post','',0),(4,1,'2013-07-03 21:15:11','2013-07-03 21:15:11','<p style=\"text-align: center;\"><a href=\"http://sf-eagle.com/storm/wp/wp-content/uploads/2013/07/4thofjuly-gayflag.jpg\"><img class=\"size-medium wp-image-5 aligncenter\" alt=\"4thofjuly-gayflag\" src=\"http://sf-eagle.com/storm/wp/wp-content/uploads/2013/07/4thofjuly-gayflag-300x157.jpg\" width=\"300\" height=\"157\" /></a></p>\r\n<p style=\"text-align: center;\">We\'ll be celebrating INDEPENDENCE in more than one way this Thursday, still high from the the ruling by the Supremes. We\'ll be serving complimentary All-American burgers and dogs to go with your choice of beverage</p>\r\n3pm - 7pm\r\nFREE FOOD\r\n$3 PINT OF ANY OF OUR DRAFTS\r\n$5 ANY FLAVOR OF ABSOLUT','4th of July BBQ','','publish','open','open','','4th-of-july-bbq','','','2013-07-04 19:33:56','2013-07-04 19:33:56','',0,'http://sf-eagle.com/storm/wp/?p=4',0,'post','',0),(5,1,'2013-07-02 18:56:38','2013-07-02 18:56:38','','4thofjuly-gayflag','','inherit','open','open','','4thofjuly-gayflag','','','2013-07-02 18:56:38','2013-07-02 18:56:38','',4,'http://sf-eagle.com/storm/wp/wp-content/uploads/2013/07/4thofjuly-gayflag.jpg',0,'attachment','image/jpeg',0),(26,1,'2013-07-05 19:32:05','2013-07-05 19:32:05','<a title=\"Something Sticky\" href=\"http://www.facebook.com/events/540813725956385/\" target=\"_blank\"><img class=\"alignnone size-medium wp-image-27\" alt=\"charliehorse\" src=\"http://sf-eagle.com/storm/wp/wp-content/uploads/2013/07/charliehorse-202x300.jpg\" width=\"202\" height=\"300\" /></a>\r\n\r\n&nbsp;\r\n\r\nA worm hole will open up at the Eagle this Saturday and suck us all through time and space ...and something sticky... to the place where Kafka, Disney, and Garbo are all tripping on acid and puffing on a Hookah. If you notice the technicolor draining from the streets of San Francisco it\'s because it will all be concentrated in our humble little bar down on 12th Street. We dare you to show up.','A Horse They Call Charlie','','publish','open','open','','a-horse-the-call-charlie','','','2013-07-12 14:19:12','2013-07-12 14:19:12','',0,'http://sf-eagle.com/storm/wp/?p=26',0,'post','',0),(6,1,'2013-07-02 18:58:04','2013-07-02 18:58:04','<p style=\"text-align: center;\"><a href=\"http://sf-eagle.com/